"use strict";var xhr=new XMLHttpRequest,listDates=function e(t){for(var n="",r=0;r<t.length;r++){var s=moment(t[r].date),a=s.diff(moment(),"milliseconds"),o=moment.duration(1,"months"),d="",l=s.fromNow(),i=humanizeDuration(a,{largest:2,round:!0});a<0?i+=" ago":i="in "+i,d=Math.abs(a)<o?l:i,n+="<tr><td>"+t[r].event+"</td><td class='num'>"+s.calendar(null,{sameElse:"ll"})+"</td><td class='num'>"+d+"</td></tr>"}return n};xhr.open("GET","data/dates.json",!0),xhr.onload=function(){if(xhr.status>=200&&xhr.status<400){var e=JSON.parse(xhr.responseText);document.getElementById("datelist-content").innerHTML=listDates(e)}else{document.getElementById("datelist-error").innerHTML="Whoops! Something went wrong. Please try again."}},xhr.onerror=function(){document.getElementById("datelist-error").innerHTML="Whoops! We couldn't reach the server. Please try again."},xhr.send();